var Core = require("leanft.sdk.core");
var ItemDragAndDropBehavior = Core.ItemDragAndDropBehavior;
var ListViewItemBaseBehavior = Core.ListViewItemBaseBehavior;
var ObjectUtils = Core.ObjectUtils;
var MergeBehavior = Core.MergeBehavior;

/**
 * A ListView item object
 * @constructor
 * @class ListViewItem
 * @mixes ItemDragAndDropBehavior._properties
 * @mixes ItemDragAndDropBehavior._methods
 * @mixes ListViewItemBaseBehavior._properties
 * @mixes ListViewItemBaseBehavior._methods
 * @memberof StdWin
 */
function ListViewItem(ownerListViewBox, identifier, textRepresentation) {
    ObjectUtils.extend(this, MergeBehavior);
    this._mergeBehavior(ItemDragAndDropBehavior);
    this._mergeBehavior(ListViewItemBaseBehavior);
    this._ownerListViewBox = ownerListViewBox;
    this._identifier = identifier;
    this._textRepresentation = textRepresentation;
    this._executeWithEventsOnItem = this._ownerListViewBox._executeWithEventsOnItem.bind(this._ownerListViewBox, this);
    this._executeWithEventsOnItemOnError = this._ownerListViewBox._executeWithEventsOnItemOnError.bind(this._ownerListViewBox, this);
}

module.exports.ListViewItem = ListViewItem;
