var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var UiWithLayoutInfo = require("./ui_object_base_with_layout_info.js");
var UiObjectWithLayoutInfoBehaviour = UiWithLayoutInfo.UiObjectWithLayoutInfoBehaviour;
var UiObjectWithLayoutInfoDescription = UiWithLayoutInfo.UiObjectWithLayoutInfoDescription;

/**
 * The description of a Static test object.
 * @param {Object} [props] Initializes a Static test object using the supplied property bag.
 * @returns {Static} the description object that was initialized.
 * @class Static
 * @memberof StdWin
 * @extends StdWin.UiObjectBaseDescription
 * @mixes StdWin.UiObjectWithLayoutInfoDescription
 */
function Static(props) {
    var staticDesc = new UiObjectBaseDescription("Static");
    Utils.extend(staticDesc, UiObjectWithLayoutInfoDescription);
    staticDesc._defineByPropertyBag(props);
    return staticDesc;
}

/**
 * A Static object
 * @constructor
 * @class StaticTO
 * @extends StdWin.UiObjectBaseTO
 * @mixes StdWin.UiObjectWithLayoutInfoBehaviour._properties
 * @memberof StdWin
 */
function StaticTO(session, description) {
    description = description || Static();
    var staticTO = new UiObjectBaseTO("Static", session, description);
    staticTO._mergeBehavior(UiObjectWithLayoutInfoBehaviour);
    return staticTO;
}

module.exports.Static = Static;
module.exports.StaticTO = StaticTO;
