var Core = require("leanft.sdk.core");
var Logger = Core.Logger;
var ObjectUtils = Core.ObjectUtils;
var ConcreteFactoryBehavior = Core.ConcreteFactoryBehavior;
var WindowTO = require("./window.js").WindowTO;
var DialogTO = require("./dialog.js").DialogTO;
var ButtonTO = require("./button.js").ButtonTO;
var UIObjectTO = require("./ui_object.js").UIObjectTO;
var EditTO = require("./edit.js").EditTO;
var ListBoxTO = require("./list_box.js").ListBoxTO;
var ListViewTO = require("./list_view.js").ListViewTO;
var TreeViewTO = require("./tree_view.js").TreeViewTO;
var ToolBarTO = require("./tool_bar.js").ToolBarTO;
var MenuTO = require("./menu.js").MenuTO;
var CheckBoxTO = require("./check_box.js").CheckBoxTO;
var RadioButtonTO = require("./radio_button.js").RadioButtonTO;
var StatusBarTO = require("./status_bar.js").StatusBarTO;
var TabControlTO = require("./tab_control.js").TabControlTO;
var ComboBoxTO = require("./combo_box.js").ComboBoxTO;
var EditorTO = require("./editor.js").EditorTO;
var ScrollBarTO = require("./scroll_bar.js").ScrollBarTO;
var SpinBoxTO = require("./spin_box.js").SpinBoxTO;
var StaticTO = require("./static.js").StaticTO;
var CalendarTO = require("./calendar.js").CalendarTO;

function StdWindowFactory(session){
    this._logger = new Logger("StdWin.TestObjectFactory");
    this._session = session;
    this._technology = "Standard Windows";

    ObjectUtils.extend(this, ConcreteFactoryBehavior);
}

StdWindowFactory.prototype = {
    _logger: null,
    _session: null,
    _technology: null,

    _constructors: {
        "window": WindowTO,
        "dialog": DialogTO,
        "button": ButtonTO,
        "uiobject": UIObjectTO,
        "editfield": EditTO,
        "listbox": ListBoxTO,
        "listview": ListViewTO,
        "treeview": TreeViewTO,
        "toolbar": ToolBarTO,
        "menu": MenuTO,
        "checkbox": CheckBoxTO,
        "radiobutton": RadioButtonTO,
        "statusbar": StatusBarTO,
        "tabcontrol": TabControlTO,
        "combobox": ComboBoxTO,
        "editor": EditorTO,
        "scrollbar": ScrollBarTO,
        "spinbox": SpinBoxTO,
        "static": StaticTO,
        "calendar": CalendarTO
    },

    _nativeClassToLeanFtType: {
        "window": "window",
        "dialog": "dialog",
        "winbutton": "button",
        "winobject": "uiobject",
        "winedit": "editfield",
        "winlist": "listbox",
        "winlistview": "listview",
        "wintreeview": "treeview",
        "wintoolbar": "toolbar",
        "winmenu": "menu",
        "wincheckbox": "checkbox",
        "winradiobutton": "radiobutton",
        "winstatusbar": "statusbar",
        "wintab": "tabcontrol",
        "wincombobox": "combobox",
        "wineditor": "editor",
        "winscrollbar": "scrollbar",
        "winspin": "spinbox",
        "static": "static",
        "wincalendar": "calendar"
    }
};
module.exports.StdWindowFactory = StdWindowFactory;