var assert = require("assert");
var sinon = require("sinon");

describe("Dialog Test Object", function () {

    var WindowTO = require("../lib/window.js").WindowTO;
    var DialogTO = require("../lib/dialog.js").DialogTO;
    var windowTOUnderTest;
    var dialogTOUnderTest

    beforeEach(function () {
        windowTOUnderTest = new WindowTO();
        dialogTOUnderTest = new DialogTO();
    });

    describe("Dialog tests", function () {
        it("should check all properties and methods of DialogTO and WindowTO are the same", function () {
            Object.keys(windowTOUnderTest).forEach(function(key){
                assert((Object.keys(dialogTOUnderTest)).indexOf(key) >= 0);
            });
        });
    });
});
