var Core = require("leanft.sdk.core");
var Logger = Core.Logger;
var ObjectUtils = Core.ObjectUtils;
var ConcreteFactoryBehavior = Core.ConcreteFactoryBehavior;
var WindowTO = require("./window.js").WindowTO;
var ScreenTO = require("./screen.js").ScreenTO;
var TextScreenTO = require("./text_screen.js").TextScreenTO;
var FieldTO = require("./field.js").FieldTO;

function TerminalEmulatorsFactory(session){
    this._logger = new Logger("TerminalEmulators.TestObjectFactory");
    this._session = session;
    this._technology = "Terminal Emulators";

    ObjectUtils.extend(this, ConcreteFactoryBehavior);
}

TerminalEmulatorsFactory.prototype = {
    _logger: null,
    _session: null,
    _technology: null,

    _constructors: {
        "window": WindowTO,
        "screen": ScreenTO,
        "textscreen": TextScreenTO,
        "field": FieldTO
    },

    _nativeClassToLeanFtType: {
        "tewindow": "window",
        "tescreen": "screen",
        "tetextscreen": "textscreen",
        "tefield": "field"
    }
};
module.exports.TerminalEmulatorsFactory = TerminalEmulatorsFactory;