var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro comboBox test object.
 * @param {Object} [props] Initializes a UIAPro ComboBox test object using the supplied property bag.
 * @returns {ComboBox} the description object that was initialized.
 * @class ComboBox
 * @extends UIAPro.UiObjectBaseDescription
 * @mixes UIAPro.SelectionDescriptionBehavior
 * @mixes UIAPro.FieldDescriptionBehavior
 * @memberof UIAPro
 */
function ComboBox(props) {
    var comboBoxDesc = new UiObjectBaseDescription("ComboBox");

    comboBoxDesc._defineByPropertyBag(props);
    return comboBoxDesc;
}

/**
 * A UIAPro comboBox.
 * @constructor
 * @class ComboBoxTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function ComboBoxTO(session, description) {
    description = description || ComboBox();
    var comboBoxTO = new UiObjectBaseTO("UIAProComboBox", session, description);
    
    return comboBoxTO;
}

module.exports.ComboBoxTO = ComboBoxTO;
module.exports.ComboBox = ComboBox;