var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro grid test object.
 * @param {Object} [props] Initializes a UIAPro Grid test object using the supplied property bag.
 * @returns {Grid} the description object that was initialized.
 * @class Grid
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function Grid(props) {
    var gridDesc = new UiObjectBaseDescription("Grid");

    gridDesc._defineByPropertyBag(props);
    return gridDesc;
}

/**
 * A UIAPro grid.
 * @constructor
 * @class GridTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function GridTO(session, description) {
    description = description || Grid();
    var gridTO = new UiObjectBaseTO("UIAProDataGrid", session, description);
    
    return gridTO;
}

module.exports.GridTO = GridTO;
module.exports.Grid = Grid;