var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro grid item test object.
 * @param {Object} [props] Initializes a UIAPro Grid Item test object using the supplied property bag.
 * @returns {GridItem} the description object that was initialized.
 * @class GridItem
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function GridItem(props) {
    var gridItemDesc = new UiObjectBaseDescription("GridItem");

    gridItemDesc._defineByPropertyBag(props);
    return gridItemDesc;
}

/**
 * A UIAPro grid item.
 * @constructor
 * @class GridItemTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function GridItemTO(session, description) {
    description = description || GridItem();
    var gridItemTO = new UiObjectBaseTO("UIAProDataItem", session, description);
    
    return gridItemTO;
}

module.exports.GridItemTO = GridItemTO;
module.exports.GridItem = GridItem;