var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro image test object.
 * @param {Object} [props] Initializes a UIAPro Image test object using the supplied property bag.
 * @returns {Image} the description object that was initialized.
 * @class Image
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function Image(props) {
    var imageDesc = new UiObjectBaseDescription("Image");

    imageDesc._defineByPropertyBag(props);
    return imageDesc;
}

/**
 * A UIAPro image.
 * @constructor
 * @class ImageTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function ImageTO(session, description) {
    description = description || Image();
    var imageTO = new UiObjectBaseTO("UIAProImage", session, description);
    
    return imageTO;
}

module.exports.ImageTO = ImageTO;
module.exports.Image = Image;