var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro hyper link test object.
 * @param {Object} [props] Initializes a UIAPro Link test object using the supplied property bag.
 * @returns {Link} the description object that was initialized.
 * @class Link
 * @extends UIAPro.UiObjectBaseDescription
 * @mixes UIAPro.FieldDescriptionBehavior
 * @memberof UIAPro
 */
function Link(props) {
    var linkDesc = new UiObjectBaseDescription("Link");

    linkDesc._defineByPropertyBag(props);
    return linkDesc;
}

/**
 * A UIAPro hyper link.
 * @constructor
 * @class LinkTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function LinkTO(session, description) {
    description = description || Link();
    var linkTO = new UiObjectBaseTO("UIAProHyperLink", session, description);
    
    return linkTO;
}

module.exports.LinkTO = LinkTO;
module.exports.Link = Link;