var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro list test object.
 * @param {Object} [props] Initializes a UIAPro List test object using the supplied property bag.
 * @returns {List} the description object that was initialized.
 * @class List
 * @extends UIAPro.UiObjectBaseDescription
 * @mixes UIAPro.SelectionDescriptionBehavior
 * @mixes UIAPro.ListDescriptionBehavior
 * @memberof UIAPro
 */
function List(props) {
    var listDesc = new UiObjectBaseDescription("List");

    listDesc._defineByPropertyBag(props);
    return listDesc;
}

/**
 * A UIAPro list.
 * @constructor
 * @class ListTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function ListTO(session, description) {
    description = description || List();
    var listTO = new UiObjectBaseTO("UIAProList", session, description);
    
    return listTO;
}

module.exports.ListTO = ListTO;
module.exports.List = List;