var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro list item test object.
 * @param {Object} [props] Initializes a UIAPro List Item test object using the supplied property bag.
 * @returns {ListItem} the description object that was initialized.
 * @class ListItem
 * @extends UIAPro.UiObjectBaseDescription
 * @mixes UIAPro.SelectionDescriptionBehavior
 * @memberof UIAPro
 */
function ListItem(props) {
    var listItemDesc = new UiObjectBaseDescription("ListItem");

    listItemDesc._defineByPropertyBag(props);
    return listItemDesc;
}

/**
 * A UIAPro list item.
 * @constructor
 * @class ListItemTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function ListItemTO(session, description) {
    description = description || ListItem();
    var listItemTO = new UiObjectBaseTO("UIAProListItem", session, description);

    return listItemTO;
}

module.exports.ListItemTO = ListItemTO;
module.exports.ListItem = ListItem;
