var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro menu test object.
 * @param {Object} [props] Initializes a UIAPro Menu test object using the supplied property bag.
 * @returns {Menu} the description object that was initialized.
 * @class Menu
 * @extends UIAPro.UiObjectBaseDescription
 * @mixes UIAPro.MenuDescriptionBehavior
 * @memberof UIAPro
 */
function Menu(props) {
    var menuDesc = new UiObjectBaseDescription("Menu");

    menuDesc._defineByPropertyBag(props);
    return menuDesc;
}

/**
 * A UIAPro menu.
 * @constructor
 * @class MenuTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function MenuTO(session, description) {
    description = description || Menu();
    var menuTO = new UiObjectBaseTO("UIAProMenu", session, description);
    
    return menuTO;
}

module.exports.MenuTO = MenuTO;
module.exports.Menu = Menu;