var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro menu bar test object.
 * @param {Object} [props] Initializes a UIAPro Menu Bar test object using the supplied property bag.
 * @returns {MenuBar} the description object that was initialized.
 * @class MenuBar
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function MenuBar(props) {
    var menuBarDesc = new UiObjectBaseDescription("MenuBar");

    menuBarDesc._defineByPropertyBag(props);
    return menuBarDesc;
}

/**
 * A UIAPro menu bar.
 * @constructor
 * @class MenuBarTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function MenuBarTO(session, description) {
    description = description || MenuBar();
    var menuBarTO = new UiObjectBaseTO("UIAProMenuBar", session, description);
    
    return menuBarTO;
}

module.exports.MenuBarTO = MenuBarTO;
module.exports.MenuBar = MenuBar;