var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro menu item test object.
 * @param {Object} [props] Initializes a UIAPro Menu Item test object using the supplied property bag.
 * @returns {MenuItem} the description object that was initialized.
 * @class MenuItem
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function MenuItem(props) {
    var menuItemDesc = new UiObjectBaseDescription("MenuItem");

    menuItemDesc._defineByPropertyBag(props);
    return menuItemDesc;
}

/**
 * A UIAPro menu item.
 * @constructor
 * @class MenuItemTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function MenuItemTO(session, description) {
    description = description || MenuItem();
    var menuItemTO = new UiObjectBaseTO("UIAProMenuItem", session, description);

    return menuItemTO;
}

module.exports.MenuItemTO = MenuItemTO;
module.exports.MenuItem = MenuItem;