var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var MergeBehavior = Core.MergeBehavior;
var Convert = Core.Convert;

/**
 * The UIA Pro Value pattern.
 * @class ValuePattern
 * @mixes UIAPro.ValuePatternBehavior._properties
 * @mixes UIAPro.ValuePatternBehavior._methods
 * @memberof UIAPro
 */
function ValuePattern(owner) {
    Utils.extend(this, MergeBehavior);
    this._mergeBehavior(ValuePatternBehavior);
    this._owner = owner;
    this._name = ValuePattern.name;
}

ValuePattern.prototype = {
    _owner: null
};

/**
 * The methods and properties of ValuePattern object
 * @memberof UIAPro
 * @ignore
 */
var ValuePatternBehavior = {
    /**
     * @memberof UIAPro.ValuePatternBehavior
     * @ignore
     */
    _properties: {
        /**
         * Checks if the value of a UIA Pro element is read-only.
         * @function
         * @returns {Promise<boolean>} a promise that is fulfilled with an indication of whether the object is read-only.
         * @memberof UIAPro.ValuePattern#
         */
        isReadOnly: function () {
            var self = this;
            var caleeArguments = arguments;

            return this._owner._executeCommand(this._name).then(function (pattern) {
                var patternOperation = function () {
                    return pattern.IsReadOnly();
                };

                var options = { sender: self._owner, onError: true, calleeArguments: caleeArguments, type: "TestObject", name: "isReadOnly" };
                return self._owner.notifyWithEvents(patternOperation, arguments, options);
            });
        },
        /**
         * Returns the value of the UIA Pro element.
         * @function
         * @returns {Promise<string>} a promise that is fulfilled with the object's value.
         * @memberof UIAPro.ValuePattern#
         */
        getValue: function () {
            var self = this;
            var caleeArguments = arguments;

            return this._owner._executeCommand(this._name).then(function (pattern) {
                var patternOperation = function () {
                    return pattern.GetValue();
                };

                var options = { sender: self._owner, onError: true, calleeArguments: caleeArguments, type: "TestObject", name: "getValue" };
                return self._owner.notifyWithEvents(patternOperation, arguments, options);
            });
        }
    },

    /**
     * @memberof UIAPro.ValuePatternBehavior
     * @ignore
     */
    _methods: {
        /**
         * Sets the value of the object.
         * @function
         * @param {string} value The value to set.
         * @returns {Promise<void>} a promise that is fulfilled when the setValue operation ends.
         * @memberof UIAPro.ValuePattern#
         */
        setValue: function (value) {
            if (value === null)
                throw new Error("You must pass parameter value.");

            var self = this;
            var caleeArguments = arguments;

            return this._owner._executeCommand(this._name).then(function (pattern) {
                var patternOperation = function () {
                    return pattern.SetValue(Convert.toString(value));
                };

                var options = { sender: self._owner, onError: true, calleeArguments: caleeArguments, type: "TestObject", name: "setValue" };
                return self._owner.notifyWithEvents(patternOperation, arguments, options);
            });
        },
        /**
         * Sets the encoded value of this object.
         * @function
         * @param {string} encodedText The encoded text to set to the object.
         * @returns {Promise<void>} a promise that is fulfilled when the setSecure operation ends.
         * @memberof UIAPro.ValuePattern#
         */
        setSecure: function (encodedText) {
            if (encodedText === null)
                throw new Error("You must pass parameter encodedText.");

            var self = this;
            var caleeArguments = arguments;

            return this._owner._executeCommand(this._name).then(function (pattern) {
                var patternOperation = function () {
                    return pattern.SetSecureValue(Convert.toString(encodedText));
                };

                var options = { sender: self._owner, onError: true, calleeArguments: caleeArguments, type: "TestObject", name: "setSecure" };
                return self._owner.notifyWithEvents(patternOperation, arguments, options);
            });
        }
    }
};

module.exports.ValuePattern = ValuePattern;