var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro scroll bar test object.
 * @param {Object} [props] Initializes a UIAPro Scroll Bar test object using the supplied property bag.
 * @returns {ScrollBar} the description object that was initialized.
 * @class ScrollBar
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function ScrollBar(props) {
    var scrollBarDesc = new UiObjectBaseDescription("ScrollBar");

    scrollBarDesc._defineByPropertyBag(props);
    return scrollBarDesc;
}

/**
 * A UIAPro scroll bar.
 * @constructor
 * @class ScrollBarTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function ScrollBarTO(session, description) {
    description = description || ScrollBar();
    var scrollBarTO = new UiObjectBaseTO("UIAProScrollBar", session, description);

    return scrollBarTO;
}

module.exports.ScrollBarTO = ScrollBarTO;
module.exports.ScrollBar = ScrollBar;