var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro slider test object.
 * @param {Object} [props] Initializes a UIAPro Slider test object using the supplied property bag.
 * @returns {Slider} the description object that was initialized.
 * @class Slider
 * @extends UIAPro.UiObjectBaseDescription
 * @mixes UIAPro.SliderDescriptionBehavior
 * @memberof UIAPro
 */
function Slider(props) {
    var sliderDesc = new UiObjectBaseDescription("Slider");

    sliderDesc._defineByPropertyBag(props);
    return sliderDesc;
}

/**
 * A UIAPro slider.
 * @constructor
 * @class SliderTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function SliderTO(session, description) {
    description = description || Slider();
    var sliderTO = new UiObjectBaseTO("UIAProSlider", session, description);

    return sliderTO;
}

module.exports.SliderTO = SliderTO;
module.exports.Slider = Slider;