var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro spinner test object.
 * @param {Object} [props] Initializes a UIAPro Spinner test object using the supplied property bag.
 * @returns {Spinner} the description object that was initialized.
 * @class Spinner
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function Spinner(props) {
    var spinnerDesc = new UiObjectBaseDescription("Spinner");

    spinnerDesc._defineByPropertyBag(props);
    return spinnerDesc;
}

/**
 * A UIAPro spinner.
 * @constructor
 * @class SpinnerTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function SpinnerTO(session, description) {
    description = description || Spinner();
    var spinnerTO = new UiObjectBaseTO("UIAProSpinner", session, description);

    return spinnerTO;
}

module.exports.SpinnerTO = SpinnerTO;
module.exports.Spinner = Spinner;