var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro split button test object.
 * @param {Object} [props] Initializes a UIAPro SplitButton test object using the supplied property bag.
 * @returns {SplitButton} the description object that was initialized.
 * @class SplitButton
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function SplitButton(props) {
    var splitButtonDesc = new UiObjectBaseDescription("SplitButton");

    splitButtonDesc._defineByPropertyBag(props);
    return splitButtonDesc;
}

/**
 * A UIAPro split button.
 * @constructor
 * @class SplitButtonTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function SplitButtonTO(session, description) {
    description = description || SplitButton();
    var splitButtonTO = new UiObjectBaseTO("UIAProSplitButton", session, description);
    
    return splitButtonTO;
}

module.exports.SplitButtonTO = SplitButtonTO;
module.exports.SplitButton = SplitButton;