var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro status bar test object.
 * @param {Object} [props] Initializes a UIAPro Status Bar test object using the supplied property bag.
 * @returns {StatusBar} the description object that was initialized.
 * @class StatusBar
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function StatusBar(props) {
    var statusBarDesc = new UiObjectBaseDescription("StatusBar");

    statusBarDesc._defineByPropertyBag(props);
    return statusBarDesc;
}

/**
 * A UIAPro status bar.
 * @constructor
 * @class StatusBarTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function StatusBarTO(session, description) {
    description = description || StatusBar();
    var statusBarTO = new UiObjectBaseTO("UIAProStatusBar", session, description);

    return statusBarTO;
}

module.exports.StatusBarTO = StatusBarTO;
module.exports.StatusBar = StatusBar;