var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro tab control test object.
 * @param {Object} [props] Initializes a UIAPro TabControl test object using the supplied property bag.
 * @returns {TabControl} the description object that was initialized.
 * @class TabControl
 * @extends UIAPro.UiObjectBaseDescription
 * @mixes UIAPro.SelectionDescriptionBehavior
 * @mixes UIAPro.TabControlDescriptionBehavior
 * @memberof UIAPro
 */
function TabControl(props) {
    var tabControlDesc = new UiObjectBaseDescription("TabControl");

    tabControlDesc._defineByPropertyBag(props);
    return tabControlDesc;
}

/**
 * A UIAPro tab control.
 * @constructor
 * @class TabControlTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function TabControlTO(session, description) {
    description = description || TabControl();
    var tabControlTO = new UiObjectBaseTO("UIAProTab", session, description);

    return tabControlTO;
}

module.exports.TabControlTO = TabControlTO;
module.exports.TabControl = TabControl;