var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro table test object.
 * @param {Object} [props] Initializes a UIAPro Table test object using the supplied property bag.
 * @returns {Table} the description object that was initialized.
 * @class Table
 * @extends UIAPro.UiObjectBaseDescription
 * @mixes UIAPro.TableDescriptionBehavior
 * @memberof UIAPro
 */
function Table(props) {
    var tableDesc = new UiObjectBaseDescription("Table");

    tableDesc._defineByPropertyBag(props);
    return tableDesc;
}

/**
 * A UIAPro table.
 * @constructor
 * @class TableTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function TableTO(session, description) {
    description = description || Table();
    var tableTO = new UiObjectBaseTO("UIAProTable", session, description);

    return tableTO;
}

module.exports.TableTO = TableTO;
module.exports.Table = Table;