var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro text test object.
 * @param {Object} [props] Initializes a UIAPro Text test object using the supplied property bag.
 * @returns {Text} the description object that was initialized.
 * @class Text
 * @extends UIAPro.UiObjectBaseDescription
 * @mixes UIAPro.FieldDescriptionBehavior
 * @memberof UIAPro
 */
function Text(props) {
    var textDesc = new UiObjectBaseDescription("Text");

    textDesc._defineByPropertyBag(props);
    return textDesc;
}

/**
 * A UIAPro text.
 * @constructor
 * @class TextTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function TextTO(session, description) {
    description = description || Text();
    var textTO = new UiObjectBaseTO("UIAProText", session, description);

    return textTO;
}

module.exports.TextTO = TextTO;
module.exports.Text = Text;