var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro tool bar test object.
 * @param {Object} [props] Initializes a UIAPro Tool Bar test object using the supplied property bag.
 * @returns {ToolBar} the description object that was initialized.
 * @class ToolBar
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function ToolBar(props) {
    var toolBarDesc = new UiObjectBaseDescription("ToolBar");

    toolBarDesc._defineByPropertyBag(props);
    return toolBarDesc;
}

/**
 * A UIAPro tool bar.
 * @constructor
 * @class ToolBarTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function ToolBarTO(session, description) {
    description = description || ToolBar();
    var toolBarTO = new UiObjectBaseTO("UIAProToolBar", session, description);

    return toolBarTO;
}

module.exports.ToolBarTO = ToolBarTO;
module.exports.ToolBar = ToolBar;