var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro tool tip test object.
 * @param {Object} [props] Initializes a UIAPro ToolTip test object using the supplied property bag.
 * @returns {ToolTip} the description object that was initialized.
 * @class ToolTip
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function ToolTip(props) {
    var toolTipDesc = new UiObjectBaseDescription("ToolTip");

    toolTipDesc._defineByPropertyBag(props);
    return toolTipDesc;
}

/**
 * A UIAPro tool tip.
 * @constructor
 * @class ToolTipTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function ToolTipTO(session, description) {
    description = description || ToolTip();
    var toolTipTO = new UiObjectBaseTO("UIAProToolTip", session, description);

    return toolTipTO;
}

module.exports.ToolTipTO = ToolTipTO;
module.exports.ToolTip = ToolTip;