var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro tree view test object.
 * @param {Object} [props] Initializes a UIAPro TreeView test object using the supplied property bag.
 * @returns {TreeView} the description object that was initialized.
 * @class TreeView
 * @extends UIAPro.UiObjectBaseDescription
 * @mixes UIAPro.TreeViewDescriptionBehavior
 * @mixes UIAPro.SelectionDescriptionBehavior
 * @memberof UIAPro
 */
function TreeView(props) {
    var treeViewDesc = new UiObjectBaseDescription("TreeView");

    treeViewDesc._defineByPropertyBag(props);
    return treeViewDesc;
}

/**
 * A UIAPro tree view.
 * @constructor
 * @class TreeViewTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function TreeViewTO(session, description) {
    description = description || TreeView();
    var treeViewTO = new UiObjectBaseTO("UIAProTree", session, description);

    return treeViewTO;
}

module.exports.TreeViewTO = TreeViewTO;
module.exports.TreeView = TreeView;