var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro object test object.
 * @param {Object} [props] Initializes a UIAPro object test object using the supplied property bag.
 * @returns {UiObject} the description object that was initialized.
 * @class UiObject
 * @extends UIAPro.UiObjectBaseDescription
 * @memberof UIAPro
 */
function UiObject(props) {
    var uiObjectDesc = new UiObjectBaseDescription("UiObject");

    uiObjectDesc._defineByPropertyBag(props);
    return uiObjectDesc;
}

/**
 * A UIAPro object.
 * @constructor
 * @class UiObjectTO
 * @extends UIAPro.UiObjectBaseTO
 * @memberof UIAPro
 */
function UiObjectTO(session, description) {
    description = description || UiObject();
    var uiObjectTO = new UiObjectBaseTO("UIAProObject", session, description);

    return uiObjectTO;
}

module.exports.UiObjectTO = UiObjectTO;
module.exports.UiObject = UiObject;