var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * The description of a UIAPro window test object.
 * @param {Object} [props] Initializes a UIAPro Window test object using the supplied property bag.
 * @returns {Window} the description object that was initialized.
 * @class Window
 * @extends UIAPro.UiObjectBaseDescription
 * @mixes UIAPro.WindowDescriptionBehavior
 * @memberof UIAPro
 */
function Window(props) {
    var windowDesc = new UiObjectBaseDescription("Window");

    windowDesc._defineByPropertyBag(props);
    return windowDesc;
}

/**
 * A UIAPro window.
 * @constructor
 * @class WindowTO
 * @extends UIAPro.UiObjectTO
 * @mixes UIAPro.WindowTOBehavior._properties
 * @mixes UIAPro.WindowTOBehavior._methods
 * @memberof UIAPro
 */
function WindowTO(session, description) {
    description = description || Window();
    var windowTO = new UiObjectBaseTO("UIAProWindow", session, description);
    
    return windowTO;
}

module.exports.WindowTO = WindowTO;
module.exports.Window = Window;