var assert = require("assert");
var sinon = require("sinon");

describe("SelectionPattern Test", function () {
    var SelectionPattern = require("../lib/patterns/selection_pattern.js").SelectionPattern;
    var name = "SelectionPattern";
    var selectionPatternUnderTest;
    var ownerMock;
    var promiseMock = { then: function (cb) { return cb; } };

    beforeEach(function () {
        ownerMock = {
            _executeCommand: sinon.stub(),
            notifyWithEvents: sinon.stub()
        };
        selectionPatternUnderTest = new SelectionPattern(ownerMock);
    });

    describe("getSelectedChildren method", function () {
        it("should execute _executeCommand 'GetSelectedChildren'", function () {
            ownerMock._executeCommand.returns(promiseMock);
            selectionPatternUnderTest.getSelectedChildren();
            sinon.assert.calledOnce(ownerMock._executeCommand);
            sinon.assert.calledWith(ownerMock._executeCommand, name);
        });
    });

    describe("canSelectMultipleItems method", function () {
        it("should execute command 'CanSelectMultipleItems'", function () {
            ownerMock._executeCommand.returns(promiseMock);
            selectionPatternUnderTest.canSelectMultipleItems();
            sinon.assert.calledOnce(ownerMock._executeCommand);
            sinon.assert.calledWith(ownerMock._executeCommand, name);
        });
    });

    describe("isSelectionRequired method", function () {
        it("should execute command 'IsSelectionRequired'", function () {
            ownerMock._executeCommand.returns(promiseMock);
            selectionPatternUnderTest.isSelectionRequired();
            sinon.assert.calledOnce(ownerMock._executeCommand);
            sinon.assert.calledWith(ownerMock._executeCommand, name);
        });
    });
});