var assert = require("assert");
var sinon = require("sinon");
 
describe("TableItemPattern Test", function () {
    var TableItemPattern = require("../lib/patterns/table_item_pattern.js").TableItemPattern;
    var name = "TableItemPattern";
    var tableItemPatternUnderTest;
    var ownerMock;
    var promiseMock = { then: function (cb) { return cb; } };

    beforeEach(function () {
        ownerMock = {
            _executeCommand: sinon.stub(),
            notifyWithEvents: sinon.stub()
        };
        tableItemPatternUnderTest = new TableItemPattern(ownerMock);
    });

    describe("getColumnHeaderItems method", function () {
        it("should execute _executeCommand 'GetColumnHeaderItems'", function () {
            ownerMock._executeCommand.returns(promiseMock);
            tableItemPatternUnderTest.getColumnHeaderItems();
            sinon.assert.calledOnce(ownerMock._executeCommand);
            sinon.assert.calledWith(ownerMock._executeCommand, name);
        });
    });

    describe("getRowHeaderItems method", function () {
        it("should execute _executeCommand 'GetRowHeaderItems'", function () {
            ownerMock._executeCommand.returns(promiseMock);
            tableItemPatternUnderTest.getRowHeaderItems();
            sinon.assert.calledOnce(ownerMock._executeCommand);
            sinon.assert.calledWith(ownerMock._executeCommand, name);
        });
    });
});