var assert = require("assert");
var sinon = require("sinon");
 
describe("ValuePattern Test", function () {
    var ValuePattern = require("../lib/patterns/value_pattern.js").ValuePattern;
    var name = "ValuePattern";
    var valuePatternUnderTest;
    var ownerMock;
    var promiseMock = { then: function (cb) { return cb; } };

    beforeEach(function () {
        ownerMock = {
            _executeCommand: sinon.stub(),
            notifyWithEvents: sinon.stub()
        };
        valuePatternUnderTest = new ValuePattern(ownerMock);
    });

    describe("isReadOnly method", function () {
        it("should execute _executeCommand 'IsReadOnly'", function () {
            ownerMock._executeCommand.returns(promiseMock);
            valuePatternUnderTest.isReadOnly();
            sinon.assert.calledOnce(ownerMock._executeCommand);
            sinon.assert.calledWith(ownerMock._executeCommand, name);
        });
    });

    describe("getValue method", function () {
        it("should execute _executeCommand 'GetValue'", function () {
            ownerMock._executeCommand.returns(promiseMock);
            valuePatternUnderTest.getValue();
            sinon.assert.calledOnce(ownerMock._executeCommand);
            sinon.assert.calledWith(ownerMock._executeCommand, name);
        });
    });

    describe("setValue method", function () {
        it("should throw error if param not set", function () {
            assert.throws(function () {
                valuePatternUnderTest.setValue();
            });
        });

        it("should throw error if param is null", function () {
            assert.throws(function () {
                valuePatternUnderTest.setValue(null);
            });
        });

        it("should throw error if param is not string", function () {
            assert.throws(function () {
                valuePatternUnderTest.setValue([1,2]);
            });
        });

        it("should execute command 'SetValue'", function () {
            ownerMock._executeCommand.returns(promiseMock);
            valuePatternUnderTest.setValue("some");
            sinon.assert.calledOnce(ownerMock._executeCommand);
            sinon.assert.calledWith(ownerMock._executeCommand, name);
        });
    });

    describe("setSecure method", function () {
        it("should throw error if param not set", function () {
            assert.throws(function () {
                valuePatternUnderTest.setSecure();
            });
        });

        it("should throw error if param is null", function () {
            assert.throws(function () {
                valuePatternUnderTest.setSecure(null);
            });
        });

        it("should throw error if param is not string", function () {
            assert.throws(function () {
                valuePatternUnderTest.setSecure({name: "ceva"});
            });
        });

        it("should execute command 'SetSecure'", function () {
            ownerMock._executeCommand.returns(promiseMock);
            valuePatternUnderTest.setSecure("some");
            sinon.assert.calledOnce(ownerMock._executeCommand);
            sinon.assert.calledWith(ownerMock._executeCommand, name);
        });
    });
});