var ObjUtils = require("leanft.sdk.core").ObjectUtils;
var ClickBehavior = require("leanft.sdk.core").ClickBehavior;
var DragAndDropBehavior = require("leanft.sdk.core").DragAndDropBehavior;
var WebBase = require("./web_testobject_base.js").WebBaseTestObject;
var WebBaseDesc = require("./web_testobject_base.js").WebBaseDescription;
var WebElement = require("./webelement.js");
var MediaControl = require("./media_control.js");

/**
 * @param {Object} [props] Initializes an AudioControl test object using the supplied property bag.
 * @returns {AudioControl} the description object that was initialized.
 * @constructor
 * @memberof Web
 * @extends Web.WebBaseDescription
 * @mixes Web.WebElementDescriptionBehaviour
 * @mixes Web.MediaControlDescriptionBehavior
 */
function AudioControl(props) {

    var audioControlDes = new WebBaseDesc("AudioControl"); // leanFTType

    ObjUtils.extend(audioControlDes, WebElement.WebElementDescriptionBehaviour);
    ObjUtils.extend(audioControlDes, MediaControl.MediaControlDescriptionBehavior);

    audioControlDes._defineByPropertyBag(props);

    return audioControlDes;
}

/**
 * A Web control that plays audio embedded in an HTML page.
 * @constructor
 * @class AudioControlTO
 * @extends Web.WebBaseTestObject
 * @mixes ClickBehavior._methods
 * @mixes DragAndDropBehavior._methods
 * @mixes Web.WebElementTOBehavior._properties
 * @mixes Web.WebElementTOBehavior._methods
 * @mixes Web.MediaControlTestObjectBehavior._properties
 * @mixes Web.MediaControlTestObjectBehavior._methods
 * @memberof Web
 */
function AudioControlTO(sesssion, description) {
    description = description || AudioControl();
    var audioControlTO = new WebBase("WebAudio", sesssion, description); // native class

    audioControlTO._mergeBehavior(ClickBehavior);
    audioControlTO._mergeBehavior(DragAndDropBehavior);
    audioControlTO._mergeBehavior(WebElement.WebElementTOBehavior);
    audioControlTO._mergeBehavior(MediaControl.MediaControlTestObjectBehavior);

    return audioControlTO;
}

module.exports.AudioControl = AudioControl;
module.exports.AudioControlTO = AudioControlTO;

