var _deviceMap = {
    "galaxy s5" : EmulatedDevice.GalaxyS5,
    "nexus 5x" : EmulatedDevice.Nexus5X,
    "nexus 6p" : EmulatedDevice.Nexus6P,
    "iphone 5" : EmulatedDevice.IPhone5,
    "iphone 6" : EmulatedDevice.IPhone6,
    "iphone 6 plus" : EmulatedDevice.IPhone6Plus,
    "ipad" : EmulatedDevice.IPad,
};

/*jshint eqnull:true */
var EmulatedDeviceConverter = {

    convert: function(emulatedDevice){
        var emulatedDeviceObj = _deviceMap[emulatedDevice.toLowerCase()];
        if (emulatedDeviceObj == null) {
            throw new Error("Predefined emulated device '" + emulatedDevice + "' not found.");
        }
        return emulatedDeviceObj;
    },
};

module.exports.EmulatedDevice = EmulatedDevice;
module.exports.EmulatedDeviceConverter = EmulatedDeviceConverter;
