var ObjUtils = require("leanft.sdk.core").ObjectUtils;
var ClickBehavior = require("leanft.sdk.core").ClickBehavior;
var DragAndDropBehavior = require("leanft.sdk.core").DragAndDropBehavior;
var WebBase = require("./web_testobject_base.js").WebBaseTestObject;
var WebBaseDesc = require("./web_testobject_base.js").WebBaseDescription;
var WebElement = require("./webelement.js");
var EditFieldBase = require("./edit_field_base.js");
var FormFieldBehavior = require("./helper_behaviors.js").FormFieldBehavior;
var IsEnabledBehavior = require("./helper_behaviors.js").IsEnabledBehavior;
var IsEnabledTOBehavior = require("./helper_behaviors.js").IsEnabledTOBehavior;

/**
 * The description of a Web FileField test object.
 * @param {Object} [props] Initializes a WebElement test object using the supplied property bag.
 * @returns {FileField} the description object that was initialized.
 * @constructor
 * @memberof Web
 * @extends Web.WebBaseDescription
 * @mixes Web.WebElementDescriptionBehaviour
 * @mixes Web.EditFieldBaseDescriptionBehavior
 * @mixes Web.FormFieldBehavior
 * @mixes Web.IsEnabledBehavior
 */
function FileField(props) {

    var fileFieldDes = new WebBaseDesc("FileField"); // leanFTType

    ObjUtils.extend(fileFieldDes, IsEnabledBehavior);
    ObjUtils.extend(fileFieldDes, WebElement.WebElementDescriptionBehaviour);
    ObjUtils.extend(fileFieldDes, EditFieldBase.EditFieldBaseDescriptionBehavior);
    ObjUtils.extend(fileFieldDes, FormFieldBehavior);

    fileFieldDes._defineByPropertyBag(props);

    return fileFieldDes;
}

/**
 * A Web edit box with an attached browse button.
 * @constructor
 * @memberof Web
 * @class FileFieldTO
 * @extends Web.WebBaseTestObject
 * @mixes Web.WebElementTOBehavior._properties
 * @mixes Web.WebElementTOBehavior._methods
 * @mixes Web.EditFieldBaseTestObjectBehavior._properties
 * @mixes Web.EditFieldBaseTestObjectBehavior._methods
 * @mixes ClickBehavior._methods
 * @mixes DragAndDropBehavior._methods
 * @mixes Web.IsEnabledTOBehavior._properties
 * @mixes WaitUntilEnabledBehavior._methods
 */
function FileFieldTO(session, description) {
    description = description || FileField();
    var fileFieldTO = new WebBase("WebFile", session, description); // native class

    fileFieldTO._mergeBehavior(ClickBehavior);
    fileFieldTO._mergeBehavior(DragAndDropBehavior);
    fileFieldTO._mergeBehavior(IsEnabledTOBehavior);
    fileFieldTO._mergeBehavior(WebElement.WebElementTOBehavior);
    fileFieldTO._mergeBehavior(EditFieldBase.EditFieldBaseTestObjectBehavior);

    return fileFieldTO;
}

module.exports.FileField = FileField;
module.exports.FileFieldTO = FileFieldTO;

