/**
 * Common methods for Link description
 * @memberof Web
  * @ignore
 */
var LinkBaseDescriptionBehavior = {
    /**
     * The location to which the browser navigates when the image is clicked.
     * @param {string} [href] When provided, this method acts as a setter and sets the href value.
     * @returns {string | Description} If no parameter is provided, the method acts as a getter and returns the current stored href value;
     * otherwise it returns 'this' for use with the builder pattern.
     */
    href: function (href) {
        return this._propertyHandler("href", href);
    },
    /**
     * The target frame of the navigation.
     * @param {string} [target] When provided, this method acts as a setter and sets the target value.
     * @returns {string | Description} If no parameter is provided, the method acts as a getter and returns the current stored target value;
     * otherwise it returns 'this' for use with the builder pattern.
     */
    target: function (target){
        return this._propertyHandler("target", target);
    }
};

/**
 * Common methods for Link test object
 * @memberof Web
 * @ignore
 */
var LinkBaseTestObjectBehavior = {
    /**
     * @memberof Web.LinkBaseTestObjectBehavior
     * @ignore
     */
    _properties: {
        /**
         * Returns the location to which the browser navigates when the link is clicked.
         * @returns {Promise<string>} a promise this is fulfilled with the location to which the browser navigates when the link is clicked.
         * @function
         */
        href: {

        },
        /**
         * Returns the target frame of the navigation.
         * @returns    {Promise<string>} a promise this is fulfilled with the target frame of the navigation. Possible values: Any valid HTML target attribute value, for example, <i>framename</i>, _blank, or _self.
         * @function
         */
        target: {

        }
    }
};

module.exports.LinkBaseDescriptionBehavior = LinkBaseDescriptionBehavior;
module.exports.LinkBaseTestObjectBehavior = LinkBaseTestObjectBehavior;
