var ObjUtils = require("leanft.sdk.core").ObjectUtils;
var ClickBehavior = require("leanft.sdk.core").ClickBehavior;
var DragAndDropBehavior = require("leanft.sdk.core").DragAndDropBehavior;
var WebBase = require("./web_testobject_base.js").WebBaseTestObject;
var WebBaseDesc = require("./web_testobject_base.js").WebBaseDescription;
var WebElement = require("./webelement.js");
var MediaControl = require("./media_control.js");

/**
 * The description of a Web VideoControl test object.
 * @param {Object} [props] Initializes the description using the supplied property bag.
 * @returns {VideoControl} the description object that was initialized.
 * @constructor
 * @memberof Web
 * @extends Web.WebBaseDescription
 * @mixes Web.WebElementDescriptionBehaviour
 * @mixes Web.MediaControlDescriptionBehavior
 */
function VideoControl(props) {

    var videoControlDes = new WebBaseDesc("VideoControl"); // leanFTType

    ObjUtils.extend(videoControlDes, WebElement.WebElementDescriptionBehaviour);
    ObjUtils.extend(videoControlDes, MediaControl.MediaControlDescriptionBehavior);

    videoControlDes._defineByPropertyBag(props);

    return videoControlDes;
}

/**
 * A Web control that plays video embedded in an HTML page.
 * @class VideoControlTO
 * @memberof Web
 * @constructor
 * @extends Web.WebBaseTestObject
 * @mixes Web.WebElementTOBehavior._properties
 * @mixes Web.WebElementTOBehavior._methods
 * @mixes ClickBehavior._methods
 * @mixes DragAndDropBehavior._methods
 * @mixes Web.MediaControlTestObjectBehavior._properties
 * @mixes Web.MediaControlTestObjectBehavior._methods
 */
function VideoControlTO(session, description) {
    description = description || VideoControl();
    var videoControlTO = new WebBase("WebVideo", session, description); // native class

    videoControlTO._mergeBehavior(ClickBehavior);
    videoControlTO._mergeBehavior(DragAndDropBehavior);
    videoControlTO._mergeBehavior(WebElement.WebElementTOBehavior);
    videoControlTO._mergeBehavior(MediaControl.MediaControlTestObjectBehavior);

    return videoControlTO;
}

module.exports.VideoControl = VideoControl;
module.exports.VideoControlTO = VideoControlTO;