var Core = require("leanft.sdk.core");
var Logger = Core.Logger;
var ObjectUtils = Core.ObjectUtils;
var ConcreteFactoryBehavior = Core.ConcreteFactoryBehavior;

var LinkTO = require("./link.js").LinkTO;
var ButtonTo = require("./button.js").ButtonTO;
var PageTO = require("./page.js").PageTO;
var WebElement = require("./webelement.js").WebElementTO;
var EditTO = require("./edit.js").EditTO;
var RadioGroupTO = require("./radio_group.js").RadioGroupTO;
var ListBoxTO = require("./list_box.js").ListBoxTO;
var CheckBoxTO = require("./check_box.js").CheckBoxTO;
var AgGridTO = require("./aggrid.js").AgGridTO;
var UiGridTO = require("./uigrid.js").UiGridTO;
var AreaTO = require("./area.js").AreaTO;
var ImageTO = require("./image.js").ImageTO;
var FrameTO = require("./frame.js").FrameTO;
var AudioControlTO = require("./audio_control.js").AudioControlTO;
var VideoControlTO = require("./video_control.js").VideoControlTO;
var FileFieldTO = require("./file_field.js").FileFieldTO;
var TableTO = require("./table.js").TableTO;
var MenuTO = require("./menu.js").MenuTO;
var TabStripTO = require("./tab_strip.js").TabStripTO;
var TreeTO = require("./tree.js").TreeTO;
var NumericFieldTO = require("./numeric_field.js").NumericFieldTO;
var SliderTO = require("./slider.js").SliderTO;
var Browser = require("./browser.js").Browser;

function WebFactory(session){
    this._logger = new Logger("Web.TestObjectFactory");
    this._session = session;
    this._technology = "Web";

    ObjectUtils.extend(this, ConcreteFactoryBehavior);
}

WebFactory.prototype = {
    _logger: null,
    _session: null,
    _technology: null,

    _constructors: {
        "link": LinkTO,
        "button": ButtonTo,
        "page": PageTO,
        "webelement": WebElement,
        "editfield": EditTO,
        "listbox": ListBoxTO,
        "radiogroup": RadioGroupTO,
        "checkbox": CheckBoxTO,
        "image": ImageTO,
        "aggrid": AgGridTO,
        "uigrid": UiGridTO,
        "area": AreaTO,
        "frame": FrameTO,
        "audiocontrol": AudioControlTO,
        "filefield": FileFieldTO,
        "videocontrol": VideoControlTO,
        "table": TableTO,
        "menu": MenuTO,
	    "tabstrip": TabStripTO,
	    "treeview": TreeTO,
	    "numericfield": NumericFieldTO,
        "slider": SliderTO,
        "browser": Browser
    },

    _nativeClassToLeanFtType: {
        "link": "link",
        "webbutton": "button",
        "page": "page",
        "webelement": "webelement",
        "webedit": "editfield",
        "weblist": "listbox",
	    "webradiogroup": "radiogroup",
        "webcheckbox": "checkbox",
        "image": "image",
        "webaggrid": "aggrid",
        "webuigrid": "uigrid",
        "webarea": "area",
        "frame": "frame",
        "webaudio": "audiocontrol",
        "webvideo": "videocontrol",
        "webfile": "filefield",
        "webtable": "table",
        "webmenu": "menu",
        "webtabstrip": "tabstrip",
        "webtree": "treeview",
	    "webnumber": "numericfield",
        "webrange": "slider",
        "browser": "browser"
    }
};
module.exports.WebFactory = WebFactory;