var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;

var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * @param {Object} [props] Initializes a Label test object using the supplied property bag.
 * @returns {Label} the description object that was initialized.
 * @constructor
 * @extends WinForms.UiObjectBaseDescription
 * @memberof WinForms
 */
function Label(props){
    var labelDesc = new UiObjectBaseDescription("Label");
    labelDesc._defineByPropertyBag(props);
    return labelDesc;
}

/**
 * A LabelTO object,
 * @constructor
 * @class LabelTO
 * @extends WinForms.UiObjectBaseTO
 * @mixes WinForms.LabelTOBehavior._properties
 * @memberof WinForms
 */
function LabelTO(session, description) {
    description = description || Label();
    var labelTO = new UiObjectBaseTO("SwfLabel", session, description);
    labelTO._mergeBehavior(LabelTOBehavior);
    return labelTO;
}

/**
 * The behaviors of the LabelTO object
 * @memberof WinForms
 * @ignore
 */
var LabelTOBehavior = {
    /**
     * @ignore
     * @memberof WinForms.LabelTOBehavior
     */
    _properties: {
        /**
         * Returns the tooltip text of the error icon associated with this object.
         * @function
         * @returns {Promise<string>} a promise that is fulfilled with the tooltip text of the error icon associated with this object.
         */
        errorText: function () {
            return this._executeWithEventsOnError(arguments, "GetErrorProviderText");
        }
    }
};

module.exports.Label = Label;
module.exports.LabelTO = LabelTO;