var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;
var ListBaseBehavior = Core.ListBaseBehavior;
var ListViewItem = require("./listview_item.js").ListViewItem;
var ListBoxDescriptionBaseBehavior = Core.ListBoxDescriptionBaseBehavior;
var ContainerBehavior = Core.ContainerBehavior;
var WrappedItemExecutorBehavior = Core.ExecuteItemsWithEventsBehavior;
var ListViewTOBaseBehavior = Core.ListViewTOBaseBehavior;

/**
 * The description of a ListView test object.
 * @param {Object} [props] Initializes a ListView test object using the supplied property bag.
 * @returns {ListView} the description object that was initialized.
 * @class ListView
 * @memberof WinForms
 * @extends WinForms.UiObjectBaseDescription
 * @mixes ListBoxDescriptionBaseBehavior
 */
function ListView(props) {
    var listViewDesc = new UiObjectBaseDescription("ListView");
    Utils.extend(listViewDesc, ListBoxDescriptionBaseBehavior);
    listViewDesc._defineByPropertyBag(props);
    return listViewDesc;
}

/**
 * A ListView object,
 * @constructor
 * @class ListViewTO
 * @extends WinForms.UiObjectBaseTO
 * @mixes ListBaseBehavior._methods
 * @mixes ListViewTOBaseBehavior._methods
 * @mixes ListViewTOBaseBehavior._properties
 * @mixes WinForms.ListViewTOBehavior._methods
 * @mixes WinForms.ListViewTOBehavior._properties
 * @memberof WinForms
 */
function ListViewTO(session, description) {
    description = description || ListView();
    var listViewTO = new UiObjectBaseTO("SwfListView", session, description);
    listViewTO._mergeBehavior(ContainerBehavior);
    listViewTO._mergeBehavior(WrappedItemExecutorBehavior);
    listViewTO._mergeBehavior(ListBaseBehavior);
    listViewTO._mergeBehavior(ListViewTOBaseBehavior);
    listViewTO._mergeBehavior(ListViewTOBehavior);
    listViewTO._minimumItemIndex = 0;
    return listViewTO;
}

/**
 * The behaviors of the ListView object
 * @memberof WinForms
 * @ignore
 */
var ListViewTOBehavior = {
    /**
     * @ignore
     * @memberof WinForms.ListViewTOBehavior
     */
    _properties: {
        /**
         * The tooltip text of the error icon associated with this object.
         * @function
         * @returns {Promise<string>} a promise that is fulfilled with the tooltip text of the error icon associated with this object.
         */
        errorText: function () {
            return this._executeWithEventsOnError(arguments, "GetErrorProviderText");
        },

        /**
         * A list of all checked items as a read-only collection object.
         * @function
         * @returns {Promise<ListViewItem[]>} a promise that is fulfilled with a list of all checked items in the listview. See {@link WinForms.ListViewItem}
         */
        checkedItems: {
            wiredName: ContainerBehavior._checkedItemsDefaultWiredName,
            getter: function () {
                return this._checkedItems(";", "\n");
            }
        },

        /**
         * The collection of items in the list as a read-only collection object.
         * @function
         * @returns {Promise<ListViewItem[]>} a promise that is fulfilled with collection of all list items in the listview. See {@link WinForms.ListViewItem}
         */
        items: {
            wiredName: ContainerBehavior._allItemsDefaultWiredName,
            getter: function () {
                return this._items("\n");
            }
        },

        /**
         * A list of all checked items as a read-only collection object.
         * @function
         * @returns {Promise<ListViewItem[]>} a promise that is fulfilled with a list of all checked item in the listview. See {@link WinForms.ListViewItem}
         */
        selectedItems: {
            wiredName: ContainerBehavior._selectedItemsDefaultWiredName,
            getter: function () {
                return this._selectedItems("\n");
            }
        }
    },

    /**
     * @ignore
     * @memberof WinForms.ListViewTOBehavior
     */
    _methods: {
        /**
         * Activates (double-clicks) the specified item in the list.
         * @function
         * @param {ListViewItem | number | string} item the specified item to activate from the list.
         * The input can be the list item, the 0-based index of the item or the name/text of the item.  See {@link WinForms.ListViewItem}
         * @param {MouseButton} [button = MouseButton.left] The button to activate. Default is the left button.
         * @returns {Promise<void>} a promise that is fulfilled when the activate operation is performed.
         */
        activateItem: function (item, button) {
            return this._activateItem(item, button);
        },

        /**
         * Deselects a list of items.
         * @function
         * @param {...(ListViewItem | number | string)} items You can pass as many items as you like, each item can be either the ListViewItem, name of the item you wish to select,
         * or the numeric index of the item in the list. See {@link WinForms.ListViewItem}
         * @returns {Promise<void>} a promise that is fulfilled when the operation is performed.
         */
        deselect: function (items) {
            return this._deselect.apply(this, arguments);
        },

        /**
         * Selects additional items from the multi-selection list.
         * @function
         * @param {...(ListViewItem | string | number)} items One or more items to add to the selection. Each item can be either a {@link WinForms.ListViewItem}, the item's name,
         * or the item's numeric index in the list.
         * @returns {Promise<void>} A promise that is fulfilled when the operation is performed.
         */
        extendSelect: function (items) {
            return this._extendSelect.apply(this, arguments);
        },

        /**
         * Selects a list of items.
         * @function
         * @param {...(ListViewItem | number | string)} items You can pass as many items as you like, each item can be either the ListViewItem, name of the item you wish to select,
         * or the numeric index of the item in the list. See {@link WinForms.ListViewItem}
         * @returns {Promise<void>} a promise that is fulfilled when the operation is performed.
         */
        select: function (items) {
            return this._select.apply(this, arguments);
        },

        /**
         * Returns an item with the given text.
         * @function
         * @param {string} text The text of the specified item.
         * @returns {ListViewItem} The ListViewItem object. See {@link WinForms.ListViewItem}
         */
        getItem: function (text) {
            return this._getItem(text);
        }
    },

    _helpers: {
        /**
         * Create an item from the identifier and textRepresentation
         * @param {number|string} identifier The identifier of the item. If it is a number, it is the 0-based index of the item.
         * @param {string} textRepresentation The text representation if the item.
         * @returns {ListViewItem} The ListViewItem object. See {@link WinForms.ListViewItem}
         * @ignore
         */
        _createItem: function (identifier, textRepresentation) {
            return new ListViewItem(this, identifier, textRepresentation);
        }
    },

    _additionalProperties: {
        itemsCount: {wiredName: ContainerBehavior._itemsCountDefaultWiredName}
    }
};

module.exports.ListView = ListView;
module.exports.ListViewTO = ListViewTO;
