var Core = require("leanft.sdk.core");
var Utils = Core.ObjectUtils;
var Convert = Core.Convert;

var UiObjectBase = require("./ui_object_base.js");
var UiObjectBaseTO = UiObjectBase.UiObjectBaseTO;
var UiObjectBaseDescription = UiObjectBase.UiObjectBaseDescription;

/**
 * @param {Object} [props] Initializes a Radio Button test object using the supplied property bag.
 * @returns {RadioButton} the description object that was initialized.
 * @constructor
 * @extends WinForms.UiObjectBaseDescription
 * @mixes WinForms.RadioButtonDescriptionBehavior
 * @memberof WinForms
 */
function RadioButton(props){
    var radiobuttonDesc = new UiObjectBaseDescription("RadioButton");
    Utils.extend(radiobuttonDesc, RadioButtonDescriptionBehavior);
    radiobuttonDesc._defineByPropertyBag(props);
    return radiobuttonDesc;
}

/**
 * RadioButtonDescriptionBehavior - the description for a Radio Button object
 * @memberof WinForms
 * @ignore
 */
var RadioButtonDescriptionBehavior = {
    /**
     * Indicates whether the radio button is checked.
     * @param {boolean} [isChecked] When provided, this method acts as a setter and sets the isChecked value.
     * @returns {boolean | Description} If no parameter is provided, the method acts as a getter and returns the current stored isChecked value;
     * otherwise it returns 'this' for use with the builder pattern.
     */
    isChecked: function (isChecked) {
        return this._propertyHandler("checked", isChecked, Convert.toBool);
    }
};

/**
 * A RadioButtonTO object,
 * @constructor
 * @class RadioButtonTO
 * @extends WinForms.UiObjectBaseTO
 * @mixes WinForms.RadioButtonTOBehavior._methods
 * @mixes WinForms.RadioButtonTOBehavior._properties
 * @memberof WinForms
 */
function RadioButtonTO(session, description) {
    description = description || RadioButton();
    var radiobuttonTO = new UiObjectBaseTO("SwfRadioButton", session, description);
    radiobuttonTO._mergeBehavior(RadioButtonTOBehavior);
    return radiobuttonTO;
}

/**
 * The behaviors of the RadioButtonTO object
 * @memberof WinForms
 * @ignore
 */
var RadioButtonTOBehavior = {
    /**
     * @ignore
     * @memberof WinForms.RadioButtonTOBehavior
     */
    _methods: {
        /**
         * Selects this radio button.
         * @function
         * @returns {Promise<void>} a promise that is fulfilled when the operation is performed.
         */
        set: function () {
            return this._executeWithEvents(arguments, "Set");
        }
    },

    /**
     * @ignore
     * @memberof WinForms.RadioButtonTOBehavior
     */
    _properties: {
        /**
         * Indicates whether this radio button is checked.
         * @function
         * @returns {Promise<boolean>} a promise that is fulfilled with the indication whether this radio button is checked.
         */
        isChecked: {
            wiredName: "checked"
        },

        /**
         * Returns the tooltip text of the error icon associated with this object.
         * @function
         * @returns {Promise<string>} a promise that is fulfilled with the tooltip text of the error icon associated with this object.
         */
        errorText: function () {
            return this._executeWithEventsOnError(arguments, "GetErrorProviderText");
        }
    }
};

module.exports.RadioButton = RadioButton;
module.exports.RadioButtonTO = RadioButtonTO;