var Core = require("leanft.sdk.core");
var Logger = Core.Logger;
var ObjectUtils = Core.ObjectUtils;
var ConcreteFactoryBehavior = Core.ConcreteFactoryBehavior;
var WindowTO = require("./window.js").WindowTO;
var UIObjectTO = require("./ui_object.js").UIObjectTO;
var ComboBoxTO = require("./combo_box.js").ComboBoxTO;
var ImageTO = require("./image.js").ImageTO;
var ProgressBarTO = require("./progress_bar.js").ProgressBarTO;
var StatusBarTO = require("./status_bar.js").StatusBarTO;
var ScrollBarTO = require("./scroll_bar.js").ScrollBarTO;
var LinkTO = require("./link.js").LinkTO;
var CheckBoxTO = require("./check_box.js").CheckBoxTO;
var RadioButtonTO = require("./radio_button.js").RadioButtonTO;
var TabStripTO = require("./tab_strip.js").TabStripTO;
var ListTO = require("./list.js").ListTO;
var EditTO = require("./edit.js").EditTO;
var ButtonTO = require("./button.js").ButtonTO;
var TreeViewTO = require("./tree_view.js").TreeViewTO; 
var MenuTO = require("./menu.js").MenuTO;
var SliderTO = require("./slider.js").SliderTO;
var ToolBarTO = require("./tool_bar.js").ToolBarTO;
var CalendarTO = require("./calendar.js").CalendarTO;
var TableTO = require("./table.js").TableTO;

function WPFFactory(session){
    this._logger = new Logger("WPF.TestObjectFactory");
    this._session = session;
    this._technology = "WPF";

    ObjectUtils.extend(this, ConcreteFactoryBehavior);
}

WPFFactory.prototype = {
    _logger: null,
    _session: null,
    _technology: null,

    _constructors: {
        "window": WindowTO,        
        "uiobject": UIObjectTO,
        "combobox": ComboBoxTO,
        "image": ImageTO,
        "progressbar": ProgressBarTO,
        "statusbar": StatusBarTO,
        "scrollbar": ScrollBarTO,
        "link": LinkTO,
        "checkbox": CheckBoxTO,
        "radiobutton": RadioButtonTO,
        "tabstrip": TabStripTO,
        "list": ListTO,
        "editfield": EditTO,
        "button": ButtonTO,
        "treeview": TreeViewTO,
        "menu": MenuTO,
        "slider": SliderTO,
        "toolbar": ToolBarTO,
        "calendar": CalendarTO,
        "table": TableTO
    },

    _nativeClassToLeanFtType: {
        "wpfwindow": "window",       
        "wpfobject": "uiobject",
        "wpfcombobox": "combobox",
        "wpfimage": "image",
        "wpfprogressbar": "progressbar",
        "wpfstatusbar": "statusbar",
        "wpfscrollbar": "scrollbar",
        "wpflink": "link",
        "wpfcheckbox": "checkbox",
        "wpfradiobutton": "radiobutton",
        "wpftabstrip": "tabstrip",
        "wpflist": "list",
        "wpfedit": "editfield",
        "wpfbutton": "button",
        "wpftreeview": "treeview",
        "wpfmenu": "menu",
        "wpfslider": "slider",
        "wpftoolbar": "toolbar",
        "wpfcalendar": "calendar",
        "wpftable": "table"
    }
};
module.exports.WPFFactory = WPFFactory;